mapper.select:
SELECT


,jiaoshi.jiaoshi_name as jiaoshiName
,jiaoshi.jiaoshi_phone as jiaoshiPhone
,jiaoshi.jiaoshi_id_number as jiaoshiIdNumber
,jiaoshi.jiaoshi_photo as jiaoshiPhoto
,jiaoshi.jiaoshi_email as jiaoshiEmail
,jiaoshi.jiaoshi_delete as jiaoshiDelete

FROM kecheng  a
left JOIN jiaoshi jiaoshi ON a.jiaoshi_id = jiaoshi.id



        and a.id in

        #{item}



        and a.kecheng_name like CONCAT('%',#{params.kechengName},'%')


        and a.kecheng_types = #{params.kechengTypes}


        and a.kecheng_da like CONCAT('%',#{params.kechengDa},'%')


          and a.kecheng_clicknum >= #{params.kechengClicknumStart}   


          and a.kecheng_clicknum <= #{params.kechengClicknumEnd}   


        and a.kecheng_clicknum = #{params.kechengClicknum}


        and (
            a.jiaoshi_id = #{params.jiaoshiId}
        )


          and a.zan_number >= #{params.zanNumberStart}   


          and a.zan_number <= #{params.zanNumberEnd}   


        and a.zan_number = #{params.zanNumber}


          and a.cai_number >= #{params.caiNumberStart}   


          and a.cai_number <= #{params.caiNumberEnd}   


        and a.cai_number = #{params.caiNumber}


        and a.kecheng_content like CONCAT('%',#{params.kechengContent},'%')


          and a.kecheng_delete >= #{params.kechengDeleteStart}   


          and a.kecheng_delete <= #{params.kechengDeleteEnd}   


        and a.kecheng_delete = #{params.kechengDelete}


          and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}




        and a.jiaoshi_id IS NOT NULL


        and jiaoshi.jiaoshi_name like CONCAT('%',#{params.jiaoshiName},'%')


        and jiaoshi.jiaoshi_phone like CONCAT('%',#{params.jiaoshiPhone},'%')


        and jiaoshi.jiaoshi_id_number like CONCAT('%',#{params.jiaoshiIdNumber},'%')


        and jiaoshi.jiaoshi_email like CONCAT('%',#{params.jiaoshiEmail},'%')


          and jiaoshi.jiaoshi_delete >= #{params.jiaoshiDeleteStart}   


          and jiaoshi.jiaoshi_delete <= #{params.jiaoshiDeleteEnd}   


        and jiaoshi.jiaoshi_delete = #{params.jiaoshiDelete}


          and UNIX_TIMESTAMP(jiaoshi.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


          and UNIX_TIMESTAMP(jiaoshi.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


        and a.insert_time = #{params.insertTime}


order by a.${params.sort} ${params.order}
